import React from 'react';
import TextField from '@material-ui/core/TextField';

class Payment extends React.Component {

    constructor(props) {
        super(props);
        this.state = {
            amount: ''
        }
    }

    handleChange(e) {
        this.setState({
            amount: e.target.value
        })
    }

    render() {
        return (
            <div>
                <TextField
                    variant="outlined"
                    margin="normal"
                    required
                    fullWidth
                    id="email"
                    label="Email Address"
                    name="email"
                    autoComplete="email"
                    autoFocus
                    value={this.state.amount}
                    onChange={this.handleChange}
                />
            </div>
        )
    }
}

export default Payment;
